package com.arati.java.basice.dowhileloop;

/**
 * @author ARATI-KOLTE
 */
public class PrintHelloMultipleTimeDoWhile {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 1;
		do {
			System.out.println("Hello");
			i++;
		}while (i <= 10);
		 
			
		

	}

}
