package com.arati.java.basics;

/**
 * @author ARATI-KOLTE
 */
public class DataTypeCharDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char cat1, cat2;

		cat1 = 'y';
		cat2 = 87;
		
		System.out.println(" cat1 and cat2 - " + (cat1 + cat2) );
		
		System.out.println("cat1 - " + cat1);
		
		System.out.println("cat2 - " + cat2);
		
		System.out.println("value of cat1 : " + cat1 +" and cat2 : " + cat2);
		
		System.out.println("value of cat1 and cat2 : " + cat1 + " " + cat2);
		

	}

}