public class DataTypeCharDemo2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch1,ch2;
		ch1 = 88;
		ch2 = 'y';
		System.out.println("sum of ch1 and ch2 = " + ( ch1 + ch2 ));
		
		System.out.println("value of ch1 = " + ch1 + " and value of ch2 = " + ch2);
		
		System.out.println("value of variables ch1 and ch2 = " + (ch1 + " " +ch2));
		
		System.out.println("value of variable ch1 = " + ch1);
		
		System.out.println("value of variable ch2 = " + ch2);

	}

}
