public class PrintDivisionDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int no1 = 50;
		int no2 = 5;
		System.out.println("division of above numbers " + no1 + " and " + no2 + " = " + ( no1 / no2 ));
		
		int division = no1 / no2;
		System.out.println("division of no1 no2 " + no1 + " and " + no2 + " = " + division);
		
		
		double noA , noB;
		noA = 10.2;
		noB = 2.1;
		System.out.println("division of point numbers " + noA + " and " + noB + " = " + (noA / noB));
				
		double noC = noA / noB;
		System.out.println("division brobar noa and nob " + noA + " and " + noB + " = " + noC);  
			
		
	

	}

}
