public class PrintMultiplyDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int numA =5;
		int numB =5;
		System.out.println("multiply of " + numA + " and " + numB + " = " + ( numA * numB ));
		
		
		
	    int multi = numA * numB;
		System.out.println("multiply of " + numA + " and " + numB + " = " + multi);
		
		
		
		double numC =2.2,numD =1.1;
		System.out.println("multiply of double " + numC + " and " + numD + " = " + ( numC * numD ));
		
		double numE = numC * numD;
		System.out.println("multiply of " + numC + " and " + numD + " = " + numE);
		
		
		
		double numberF = numC * numA;
		System.out.println("multiply of point no with normal no " + numC + " and " + numA + " = " + numberF);
		
		
		
		int numberG =  (int) (numA * numC);
		System.out.println("multiply of normal no with point no " + numA + " and " + numC + " = " + numberG);
		
		
		
		double noA = 55.2 , noB = 2;
		System.out.println("multiply with float no " + noA + " and " + noB + " = " + ( noA * noB ));
		
			
		
		
			             
		
		
		
		
		

	}

}
