package com.arati.java.basics;

/**
 * @author ARATI-KOLTE
 */
public class SubstractionDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number =50;
		int number1 = 10;
		
		System.out.println("sub of " + number + " and " + number1+ " = " + (number - number1));
		
		int sub = number - number1;
		
		System.out.println("sub of " + number + " and " + number1 + " = " + sub);
		
		double num2 , num3 ;
		
		num2 = 50.10;
		num3 = 40.5;
		
		System.out.println("sub of " + num2 + " and " + num3 + " = " + (num2 - num3));
		
		double num4 = num2 - num3;
		
		System.out.println("sub of " + num2 + " and " + num3 + " = " + num4);
		
		int num10 = (int) (num2 - num3);
		
		System.out.println("sub of point number in int " + num2 + " and " + num3 + " = " + num10);
		
		double num20 = (num2 - number1);
		
		System.out.println("substraction of double and normal number " + num2 + " and " + number1 + " = " + num20);
		
		
		
		
		
		
		
		
		
	

	}

}
