package com.arati.java.basics.revision;

/**
 * @author ARATI-KOLTE
 */
public class OperatorsRelational {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 6;
		int y = 7;
		
		//greator than and less than
		
		boolean result = x > y;
		boolean result1 = x < y;
		
		System.out.println(result);
		System.out.println(result1);
		
		//either less or equal
		//either greator or equal
		//equal
		//not equal
		
		int a = 2;
		int b = 2;
		boolean result2 = a <= b;
		boolean result3 = a >= b;
		boolean result4 = a == b;
		boolean result5 = a != b;
		System.out.println(result2);
		System.out.println(result3);
		System.out.println(result4);
		System.out.println(result5);
		
		
		
		
		
		
		
		
		
		

	}

}
